if (topic.match("Colour"))
 {
    h1=payload.replace("rgb(","");
    h1=h1.replace(")","");
    h2 = h1.split(",",3);
    red   = h2[0];
    green = h2[1];
    blue  = h2[2];
    flow.set("RED", red);
    flow.set("GREEN", green);
    flow.set("BLUE", blue);
 }

if (topic.match("Bright"))
 {
   flow.set("BRIGHT", payload);
 }

if (topic.match("Intense"))
 {
   flow.set("INTENSERAW", payload);
 }

if (topic.match("White"))
 {
   flow.set("WHITERAW", payload);
 }

int = Math.round(flow.get("INTENSERAW") * flow.get("BRIGHT")) ;
wht = Math.round(flow.get("WHITERAW") * flow.get("BRIGHT"));
flow.set("INTENSE", int);
flow.set("WHITE", wht); 

set =
{
    "mode": "color",    /* "color" */
    "red": flow.get("RED") ,           /* red brightness, 0..255 */
    "green": flow.get("GREEN"),         /* green brightness, 0..255 */
    "blue": flow.get("BLUE"),        /* blue brightness, 0..255 */
    "gain": flow.get("INTENSE"),        /* gain for all channels, 0..100 */
    "white": flow.get("WHITE"),         /* white brightness, 0..255 */
    "effect": 0,        /* applies an effect when set */
    "turn": "on"        /* "on", "off" or "toggle" */
};

//command = flow.get("RED")  +"-"+ flow.get("GREEN") +"-"+ flow.get("BLUE") +"-"+ flow.get("WHITE")  +"-"+ flow.get("INTENSE");
msg.payload = set;
msg.topic = "set";
return[msg];

